import React from "react";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";

const NewItems = () => {
  const items = [
    {
      imgsrc:
        "https://res.cloudinary.com/robcg1102/image/upload/v1604355699/maq_img/1_q9bcwj.jpg",
      content: "Martillo Truper",
      price: 205.68,
    },
    {
      imgsrc:
        "https://res.cloudinary.com/robcg1102/image/upload/v1604355699/maq_img/2_hh4aon.jpg",
      content: "Cable bimetal unipolar calibre 10 rojo Voltmex",
      price: 405.18,
    },
    {
      imgsrc:
        "https://res.cloudinary.com/robcg1102/image/upload/v1604355699/maq_img/3_scnswx.jpg",
      content: "Compresor de banda de 50 lts COM-3450 Oakland",
      price: 4508.46,
    },
    {
      imgsrc:
        "https://res.cloudinary.com/robcg1102/image/upload/v1604355699/maq_img/4_bkdfqv.jpg",
      content: "Carreta roja, UIF060-B3",
      price: 1040.75,
    },
    {
      imgsrc:
        "https://res.cloudinary.com/robcg1102/image/upload/v1604355699/maq_img/5_pd2zna.jpg",
      content:
        "Atornillador inalámbrico 6V con estuche, batería y cargador DCF060-B3",
      price: 1215.63,
    },
    {
      imgsrc:
        "https://res.cloudinary.com/robcg1102/image/upload/v1604355699/maq_img/6_yndnyl.jpg",
      content:
        "Atornillador inalámbrico 6V con estuche, batería y cargador DCF060-B3",
      price: 1215.63,
    },
  ];

  return (
    <div>
      <h3>Nuevos productos</h3>
      <hr />
      <div className="new_items">
        {items.map((item, index) => (
          <Card className="root">
            <CardActionArea>
              <img src={item.imgsrc} alt="..." />
              <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                  Lizard
                </Typography>
                <Typography
                  variant="body2"
                  component="p"
                  className="contentItem"
                >
                  {item.content}
                </Typography>
              </CardContent>
            </CardActionArea>
            <CardActions className="priceTag">
              <p>${item.price}</p>
              <Button size="small" color="primary">
                <i className="ri-shopping-cart-2-fill"></i> Agregar al carrito
              </Button>
            </CardActions>
          </Card>
        ))}
      </div>
      <p><Button size="small" style={{textAlign: "right"}}>
        Ver más <i class="ri-arrow-right-line"></i>
      </Button></p>
      
    </div>
  );
};

export default NewItems;
